definition.script:
#!/usr/bin/env groovy
pipeline {
    agent any
    stages {
        stage("PREREQUIS") {
            steps {
                sh '''#!/bin/bash
                cd ~/HOME/AtlanticWakePark; git fetch origin master && git reset --hard origin/master; npm install
                '''
            }
        }
        stage("BUILD") {
            steps {
                sh '''#!/bin/bash
                cd ~/HOME/AtlanticWakePark; /usr/bin/cross-env ENV='dev' ionic cordova build browser --prod
                '''
            }
        }
        stage("COPY") {
            steps {
                sh '''#!/bin/bash
                sudo rm -rf /var/www/html/AtlanticWakePark/admin/*
                cd ~/HOME/AtlanticWakePark; sudo cp -r platforms/browser/www/* /var/www/html/AtlanticWakePark/admin/
                '''
            }
        }
        stage("CHOWN CHMOD") {
            steps {
                sh '''#!/bin/bash
                cd /var/www/html/AtlanticWakePark/admin; sudo chown aloa:www-data . -R
                '''
            }
        }
    }
}
