definition.script:
#!/usr/bin/env groovy
pipeline {
    agent any
    stages {
        stage('Update') {
            steps {
                sh '''#!/bin/bash
                cd /var/www/html/AloaMobile/aloaIonic
                git pull origin master
                npm install
                '''
            }
        }
        stage('Build') {
            steps {
                sh '''#!/bin/bash
                sudo runuser -l aloa -c '\''cd /var/www/html/AloaMobile/aloaIonic; ionic cordova build browser --prod --release'\''
                '''
            }
        }

        stage('Deploy'){
            steps {
                sh '''#!/bin/bash
                sudo runuser -l aloa -c '\''cp -r /var/www/html/AloaMobile/aloaIonic/platforms/browser/www/. /var/www/html/AloaMobile'\''
                '''
            }
        }
    }
}
