definition.script:
#!/usr/bin/env groovy
pipeline {
    agent any
    stages {
        stage('Update') {
            steps {
                sh '''#!/bin/bash
                cd /var/www/html/AloaMonitoring/Monitoring_Ionic
                sudo git pull origin master
                sudo npm install
                '''
            }
        }
        stage('Build') {
            steps {
                sh '''#!/bin/bash
                cd /var/www/html/AloaMonitoring/Monitoring_Ionic
                sudo chown dev:www-data . -R
                sudo runuser -l dev -c '\''cd /var/www/html/AloaMonitoring/Monitoring_Ionic; ionic cordova build browser --prod --release'\''
                '''
            }
        }

        stage('Deploy'){
            steps {
                sh '''#!/bin/bash
                sudo runuser -l dev -c '\''cp -r /var/www/html/AloaMonitoring/Monitoring_Ionic/platforms/browser/www/. /var/www/html/AloaMonitoring'\''
                '''
            }
        }
    }
}
