definition.script:
#!/usr/bin/env groovy

import groovy.json.JsonOutput

def slackNotificationChannel = 'reports'

def notifySlack(text, channel, attachments) {
	def slackURL = 'https://hooks.slack.com/services/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'
		def jenkinsIcon = 'https://wiki.jenkins-ci.org/download/attachments/2916393/logo.png'

		def payload = JsonOutput.toJson([text: text,
				channel: channel,
				username: "Jenkins NS36",
				icon_url: jenkinsIcon,
				attachments: attachments
		])

		sh "curl -X POST --data-urlencode \'payload=${payload}\' ${slackURL}"
}

timestamps {
	node () {
		try{
			stage ('NS36 - Requirments and build') {
				sh '''export ANDROID_HOME=/var/lib/jenkins/Android/sdk
export PATH=$PATH:$ANDROID_HOME/tools
export PATH=$PATH:$ANDROID_HOME/platform-tools
cd /var/lib/jenkins/HOME/Tripapyrus_planning_android_releases;
git pull origin master;
npm install;
ionic cordova build android --prod
scp /var/lib/jenkins/HOME/Tripapyrus_planning_android_releases/platforms/android/app/build/outputs/apk/debug/app-debug.apk tripapyrpa@ftp.tripapyrus.com:/homez.2015/tripapyrpa/planning/android/app.apk'''
			}
		} catch (e) {
			notifySlack("[Tripapyrus] Problème lors de la génération du APK", slackNotificationChannel, [])
			sh "exit 1"
		}
	}
}
