definition.script:
#!/usr/bin/env groovy

import groovy.json.JsonOutput

def slackNotificationChannel = 'reports'

def notifySlack(text, channel, attachments) {
	def slackURL = 'https://hooks.slack.com/services/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'
		def jenkinsIcon = 'https://wiki.jenkins-ci.org/download/attachments/2916393/logo.png'

		def payload = JsonOutput.toJson([text: text,
				channel: channel,
				username: "Jenkins NS300",
				icon_url: jenkinsIcon,
				attachments: attachments
		])

		sh "curl -X POST --data-urlencode \'payload=${payload}\' ${slackURL}"
}

timestamps {
	node () {
		try{
		    stage('Génération des BL') {
		        sh 'ssh aloa@vps490005.ovh.net \'cd /var/www/Tripapyrus; php bin/console app:exportEbpBl;\''
			}
			stage('Copie des BL') {
			    sh 'ssh aloa@vps490005.ovh.net \'scp /var/www/Tripapyrus/public/exports/* tripapyrpa@ftp.tripapyrus.com:/homez.2015/tripapyrpa/planning_bl_to_ebp/\''
			}
		} catch (e) {
			notifySlack("[TRIPAP] Problème de copie des BL", slackNotificationChannel, [])
			sh "exit 1"
		}
	}
}
