definition.script:
#!/usr/bin/env groovy
pipeline {
    agent any
    stages {
        stage("Récupération des fichiers export EBP") {
            steps {
                parallel (
                    "Récupération" : {
                        build("Aloa - TRIPAP - Routine - Récupération des exports EBP servTripap vers servProxmox")
                    }
                )
            }
        }
        stage("Lancement des imports article et client") {
            steps {
                parallel (
                    "Import Article" : {
                        build("Aloa - TRIPAP - Routine - Import Article")
                    },
                    "Import Client" : {
                        build("Aloa - TRIPAP - Routine - Import Client")
                    }
                )
            }
        }
        stage("Lancement des imports liaison article client") {
            steps {
                parallel (
                    "Import Client Article" : {
                        build("Aloa - TRIPAP - Routine - Import Client_Article")
                    }
                )
            }
        }
    }
}
