declare -u UPPER_LF="ScicLaVergne" mkdir -p $HOME/HOME/_Rapports/$UPPER_LF ~/.timotheeBin/phploc wp-content/themes > $HOME/HOME/_Rapports/$UPPER_LF/rapport_phploc.txt ~/.timotheeBin/phpmetrics wp-content/themes --report-html=$HOME/HOME/_Rapports/$UPPER_LF/rapport_phpmetrics
